import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [sourceLang, setSourceLang] = useState('en');
  const [targetLang, setTargetLang] = useState('ja');
  const [processedImage, setProcessedImage] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const languageOptions = [
    { value: 'en', label: 'English' },
    { value: 'ja', label: 'Japanese' }
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError('Please select a file first');
      return;
    }

    setLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('file', file);
    formData.append('source_lang', sourceLang);
    formData.append('target_lang', targetLang);

    try {
      const response = await axios.post('http://localhost:8000/process/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setProcessedImage(response.data.processed_image);
    } catch (err) {
      setError('Processing failed. Please check the file and try again.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Document Translator</h1>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Upload File (PDF/PNG):</label>
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            accept=".png,.pdf"
          />
        </div>

        <div className="form-group">
          <label>Source Language:</label>
          <select
            value={sourceLang}
            onChange={(e) => setSourceLang(e.target.value)}
          >
            {languageOptions.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Target Language:</label>
          <select
            value={targetLang}
            onChange={(e) => setTargetLang(e.target.value)}
          >
            {languageOptions.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Processing...' : 'Translate Document'}
        </button>

        {error && <div className="error">{error}</div>}
      </form>

      {loading && (
        <div className="progress">
          <p>Processing document...</p>
          <progress max="100" />
        </div>
      )}

      {processedImage && (
        <div className="result">
          <h2>Processed Result:</h2>
          <div className="image-container">
            <img
              src={`http://localhost:8000${processedImage}`}
              alt="Processed document"
            />
            <a
              href={`http://localhost:8000${processedImage}`}
              download
              className="download-btn"
            >
              Download Processed Image
            </a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
