import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState('');
  const [sourceLang, setSourceLang] = useState('ja');
  const [targetLang, setTargetLang] = useState('en');
  const [processedImage, setProcessedImage] = useState('');
  const [translatedTexts, setTranslatedTexts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const languageOptions = [
    { value: 'en', label: 'English' },
    { value: 'ja', label: 'Japanese' }
  ];

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setPreview(URL.createObjectURL(selectedFile));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError('Please select a file first');
      return;
    }

    setLoading(true);
    setError('');
    setProcessedImage('');
    setTranslatedTexts([]);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('source_lang', sourceLang);
    formData.append('target_lang', targetLang);

    try {
      const response = await axios.post('http://localhost:8000/process/', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      setProcessedImage(`http://localhost:8000${response.data.processed_image}`);
      setTranslatedTexts(response.data.translated_texts || []);
    } catch (err) {
      setError('Processing failed. Please check the file and try again.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App" style={{ padding: '2rem', fontFamily: 'Arial' }}>
      <h1>ðŸ“˜ Manga Panel Translator</h1>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Upload File (.png, .jpg, .jpeg):</label><br />
          <input
            type="file"
            accept=".png,.jpg,.jpeg"
            onChange={handleFileChange}
          />
        </div>

        <div className="form-group">
          <label>Source Language:</label>
          <select value={sourceLang} onChange={(e) => setSourceLang(e.target.value)}>
            {languageOptions.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Target Language:</label>
          <select value={targetLang} onChange={(e) => setTargetLang(e.target.value)}>
            {languageOptions.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Processing...' : 'Translate'}
        </button>

        {error && <div style={{ color: 'red', marginTop: '1rem' }}>{error}</div>}
      </form>

      {preview && (
        <div style={{ marginTop: '2rem' }}>
          <h3>Original Preview:</h3>
          <img src={preview} alt="Preview" style={{ maxWidth: '100%', height: 'auto' }} />
        </div>
      )}

      {processedImage && (
        <div className="result" style={{ marginTop: '2rem' }}>
          <h2>Processed Image:</h2>
          <img
            src={processedImage}
            alt="Processed"
            style={{ maxWidth: '100%', maxHeight: '500px' }}
          />
          <div>
            <a
              href={processedImage}
              download
              className="download-btn"
              style={{ display: 'inline-block', marginTop: '1rem', padding: '0.5rem 1rem', backgroundColor: '#007bff', color: '#fff', textDecoration: 'none' }}
            >
              Download Processed Image
            </a>
          </div>

          {translatedTexts.length > 0 && (
            <div style={{ marginTop: '1rem' }}>
              <h3>Detected & Translated Texts:</h3>
              <ul>
                {translatedTexts.map((item, idx) => (
                  <li key={idx}>
                    <strong>{item.text}</strong> (x: {item.position.x}, y: {item.position.y})
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
